import React, {Component} from 'react'
import {RiCalendarTodoFill} from 'react-icons/ri'
import JugPuzzle from "./JugPuzzle"
import Main from "../FunComponent/Hatchways/Main"
import TimePicker from 'rc-time-picker'
import 'rc-time-picker/assets/index.css'
class AgendaProject extends Component{
    constructor(){
        super()
        this.state = {
            time: "",
            task: "",
            list: [],
            id: 1,
            isChecked: false,
            message: ""

        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleClear = this.handleClear.bind(this)
        this.handleOverflow =  this.handleOverflow.bind(this)
        this.setTime =  this.setTime.bind(this)

    }

    handleChange(event){

        const{name, value, type, checked} = event.target

        type === "checkbox" && this.state.list.task === name?
        this.setState({
            [name]: checked     
        }):
        name === "time"?
        this.setState({
            time: event.target.value
        }):
        this.setState({
            [name] : value,

        })
    }

    handleSubmit(event){

        if(this.state.task !== "" && this.state.time !== ""){
            this.setState({
                id: this.state.id + 1,
                task: "",
                time: "",
                message:"",
                list: this.state.list.concat([{id: this.state.id, 
                                value: this.state.time + ": " + this.state.task, 
                                completed: this.state.isChecked}]),


            })

        }else{
            this.setState({
                message: "Please enter time and task."
            })
        }
        event.preventDefault()
       /*  how to catch no entry */
    }

    handleClear(event){

        this.setState({

            task: "",
            list: [],
            id: 1,
        })
        event.preventDefault()
    }
    handleOverflow(){
        this.setState({
            list:[]
        })
    }
    setTime(value){
        const s = ":mm"
        const t = "HH"
        const q = " a"
        if(value !== null && value.format(t) > 12 ){
            console.log(value.format(t)-12 + value.format(s) + value.format(q))
            this.setState({
                time: value.format(t)-12 + value.format(s) + value.format(q)
            })
        }
        else if(value !== null)
            this.setState({
                time: value.format(t) + value.format(s) + value.format(q)
            })
    }

    render(){

        return(

            <div id = "agendaproject" className = "agenda_project">

                <form className = "agenda-form" onSubmit = {this.handleSubmit}>
                    <h1 className="h1_agenda">Agenda <RiCalendarTodoFill/></h1>

                        <TimePicker 
                        name = "time"
                        showSecond = {false} 
                        use12Hours = {true} 
                        popupClassName = "time_popup"
                        placeholder = "Enter Time"
                        onChange = {this.setTime}
                        className = "time_picker"/>


                    <input className = "task_input"
                        type = "text"
                        value = {this.state.task}
                        name = "task"
                        placeholder = "Enter Task" 
                        onChange = {this.handleChange}
                    />
                    <br/>
                    <button className = "button_add" type = "submit"> Add </button>
                    <br/>
                    <button className = "button_clear" onClick = {this.handleClear}> Clear </button>

                </form>

                <div className = "display_todo">

                    <p>{this.state.message}</p>

                    {this.state.list.length < 14 ? this.state.list.map((item)=> 

                        <label key = {item.id}>
                        <input type = "Checkbox"

                            name = "isChecked"
                            checked = {this.state.list.completed}
                            onChange = {this.handleChange}

                            />
                            {item.value}
                            <br/>
                        </label>


                    )  : this.handleOverflow()}

                </div>
                <JugPuzzle/>
                <Main/>
            </div>
        )


    }

}
export default AgendaProject
