import React from 'react'
class Tag extends React.Component{
    constructor(props){
        super(props)
        this.state={
            tag: "",
            showTag: false,
            list: [],
            tags: []
        }
        this.add = this.add.bind(this)
        this.submit = this.submit.bind(this)

    }

add(event){
    event.preventDefault()
    const{value} = event.target
    this.setState({
        tag: value,

    })

}

submit(event){
    event.preventDefault()
    if(!this.state.tags.includes(this.state.tag)){
        this.setState({
            tags: [...this.state.tags, this.state.tag],
            tag:""
        })
    }else{
        this.setState({
            tag:""
        })
    }

    this.props.callBack(this.state.tags, this.props.l)


}

render(){
    return(
        <div>
            <div className = "display_tag">
                {this.state.tags.map(current => <p className = "ind_tag" key = {current}>{current}</p>)}
            </div>
            <form onSubmit = {this.submit} >
                <input 
                    type = "text"
                    placeholder = "Add a tag"
                    value = {this.state.tag}
                    onChange = {this.add} 
                    className = "tag"
                />
            </form>
        </div>
    )
}
}

export default Tag
