import React from "react"
class Expand extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            expand_collapse: [false, false, false,false, false, false,false, false, false,false, false, false,false, false, false,false, false, false,false, false, false,false, false, false,false],
            indexNoDup: "",
        }
        this.expand = this.expand.bind(this)
        this.collapse = this.collapse.bind(this)

    }
    expand(event){
        event.preventDefault()


        const newEC = this.state.expand_collapse.slice()
        newEC[event.target.value] = true
        const g = this.props.li[event.target.value].grades.slice()

        const d = g.map(c=>g.indexOf(c, g.indexOf(c)+1))
        const d2 = d.filter(c=>c!==-1).pop()

        const i = g.map(i=>g.indexOf(i))
        i[d2] = d2

        this.setState({
            expand_collapse: newEC,
            indexNoDup:i,


        })
    }

    collapse(event){
        event.preventDefault()
        const newECC = this.state.expand_collapse.slice()
        newECC[event.target.value] = false
        this.setState({
            expand_collapse: newECC,
        })
    }




    render(){
        return(
            <div className = "test_scores">

                    <button className = "expand_button"  
                        value = {this.props.l} 
                        onClick = {this.state.expand_collapse[this.props.l] ? this.collapse : this.expand}>
                            {this.state.expand_collapse[this.props.l] ? "−" : "+"} 
                    </button>
                    {this.state.expand_collapse[this.props.l]  ? 
                        <div>
                            {this.state.indexNoDup.map(current => <p key = {current}>Test {current+1}: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {this.props.li[this.props.l].grades[current]}%</p>)}
                        </div>
                    : ""}





            </div>
        )
    }


}
export default Expand
