import React, {Component} from 'react'

class MainPage extends Component{
    constructor(){
        super()
        this.state = {
            firstName: "",
            showName: false
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleClick = this.handleClick.bind(this)
    }

    handleChange(event){
        const{name, value} = event.target
        this.setState({[name]:value })
    }
    handleClick(event){
        /* this.setState(prevState => {
            return {
                showName: !prevState.showName
            }
        }) */
        event.preventDefault()
        this.setState({
            showName: true

        });


    }


    render(){
        return(

            <div id = "mainpage">


                <h1 className = "h1MainPage">
                    Welcome to Ang's website

                </h1>

                <h2 className = "FNquestion"> Please enter your first name</h2>
                <form className = "FirstNameForm">

                    <input 
                        className = "InputFN"
                        type = 'text'
                        placeholder = "First Name"
                        value = {this.state.firstName}
                        name = "firstName"
                        onChange = {this.handleChange}
                        />
                    <button className = "enterb" onClick = {this.handleClick}>Enter</button>
                </form>
                {this.state.showName ? <h2 className = "FNmessage">Nice to meet you {this.state.firstName}</h2> : null}
                <br/>                 
                <hr/>
                <div className = "ContactInfo" data-aos="fade-down"></div>
                {/* <div className = "ContactInfo">
                    <h2>Contact Info</h2>
                    <a className = "LinkedinLink"  href="https://www.linkedin.com/in/ang-tsog" target = "_blank"> LinkedIn</a>
                    <br/>
                    <a className = "EmailLink" href="mailto:angarag97@gmail.com"> Email</a>


                </div> */}


            </div>

        )

    }

}

export default MainPage
