import React, {Component} from 'react'
import {Link} from "react-scroll"

class NavBar extends Component {
    constructor(){
        super()
        this.state = {
            show: true,
            scrollPos: 0 
        }
    }
    componentDidMount(){
        window.addEventListener('scroll', this.handleScroll)
    }
    componentWillUnmount(){
        window.removeEventListener('scroll', this.handleScroll )
    }
    handleScroll=()=>{
        this.setState({
            scrollPos: document.body.getBoundingClientRect().top,
            show: document.body.getBoundingClientRect().top > this.state.scrollPos
        })
    }

    render(){
        return(
            <div>
            {console.log(this.state)}
            <ul className = "navbar">
                        <Link 
                            activeClass = "active"
                            to = "mainpage"
                            spy = {true}
                            smooth = {true}
                            offset = {-70}
                            duration={500}>
                            Main Page
                        </Link>

                        <Link 
                            activeClass = "active"
                            to = "skills"
                            spy = {true}
                            smooth = {true}
                            offset = {-70}
                            duration={500}>
                            Skills
                        </Link>

                        <Link 
                            activeClass = "active"
                            to = "education"
                            spy = {true}
                            smooth = {true}
                            offset = {-70}
                            duration={700}>
                            Education
                        </Link>
                    </ul>
                    <nav>NavBar</nav>
            </div>

        )
    }


}

export default NavBar
