import React, {Component} from 'react'

class MainPage extends Component{
    constructor(){
        super()
        this.state = {
            firstName: "",
            showName: false
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleClick = this.handleClick.bind(this)
    }

    handleChange(event){
        const{name, value} = event.target
        this.setState({[name]:value })
    }
    handleClick(event){
        /* this.setState(prevState => {
            return {
                showName: !prevState.showName
            }
        }) */
        event.preventDefault()
        this.setState({
            showName: true

        });


    }


    render(){
        return(

            <div id = "mainpage" className = "main_page" > 


                <h1 className = "h1_main_page">
                    My name is Ang Tsog

                </h1>


                <h2 className = "h2_main_page"> Please enter your first name</h2>
                <form className = "first_name_form">

                    <input 
                        className = "input_first_name"
                        type = 'text'
                        placeholder = "First Name"
                        value = {this.state.firstName}
                        name = "firstName"
                        onChange = {this.handleChange}
                        />
                    <button className = "enter_button" onClick = {this.handleClick}>Enter</button>
                </form>
                {this.state.showName ? <h2 className = "first_name_message">Nice to meet you {this.state.firstName}</h2> : null}
                <br/>                 
                <hr/>
                <div className = "contact_info">

                    <h2>Contact Info</h2>
                    <a className = "linkedin_link"  
                        href="https://www.linkedin.com/in/ang-tsog" 
                        target = "_blank"> 
                        <img require src = "./images/linkedin.png" alt= "linkedinlogo"/>
                    LinkedIn
                    </a>

                    <br/>
                    <a className = "email_link" href="mailto:angarag97@gmail.com">    
                        <img src = "./images/email.png" alt = "emaillogo"/>        
                    Email
                    </a>


                </div>


            </div>

        )

    }

}

export default MainPage
