function AgendaProject(){

    const[task, setTask] = useState('')
    const[tasklist, setTaskList] = useState([])
    const[isChecked, setChecked] = useState(false)
    const[message, setMessage] = useState('')
    const[id, setId] = useState(0)

    const handleSubmit = (event) =>{
        event.preventDefault()

        if(task !== ""){
            setId(id+1)
            setTaskList(tasklist.concat({id:id,value:task,completed:isChecked}))
        }else{
            setMessage('Please enter a task')
        }
    }

    return(

        <div id = "agendaproject" className = "agenda_project">

            <form className = "agenda-form" onSubmit = {handleSubmit}>
                <h1 className="h1_agenda">Agenda <RiCalendarTodoFill/></h1>

                    <input className = "task_input"
                        type = "text"
                        value = {task}
                        placeholder = "Enter Task" 
                        onChange = {e=>setTask(e.target.value)}
                        />
                    <div className = 'button-wrapper'>
                        <button className = "button_add" type = "submit"> Add </button>
                        <button className = "button_clear" onClick = {e=>setTask('')}> Clear </button>
                    </div>
            </form>

            <div className = "display_todo">

                <p>{message}</p>

                    {tasklist.length < 14 ? tasklist.map((item)=> 
                        <ul key = {item.id}>

                        <input type = "Checkbox"           
                            checked ={isChecked}
                            value = {isChecked}
                            onChange = {()=>setChecked(!isChecked)+console.log(isChecked)}
                            />
                            {item.value}
                        </ul>
                        )  : ()=>setTask('')}
                        {console.log(tasklist)}
            </div>
            <JugPuzzle/>
            <Main/>
            <CurrencyExchange/>
            <OtherConversions/>
        </div>

    )
