import React, {useState} from 'react'
import {RiCalendarTodoFill} from 'react-icons/ri'
import JugPuzzle from "./JugPuzzle"
import Main from "../FunComponent/Hatchways/Main"
import CurrencyExchange from './Conversions/CurrencyExchange'
import OtherConversions from './Conversions/OtherConversions'


function AgendaProject(){

    const{task, setTask} = useState('')
    const{tasklist, setTaskList} = useState([])
    const{isChecked, setChecked} = useState(false)
    const{message, setMessage} = useState('')
    const{i, setId} = useState(0)

    const handleSubmit = (event) =>{
        event.preventDefault()

        if(task !== ""){
            setTaskList([...{id:setId(i+1),value:task,completed:isChecked}])
        }else{
            setMessage('Please enter a task')
        }
        console.log(tasklist)
    }
    const handleClear = (event) =>{
        setTask('')
    }
    return(

        <div id = "agendaproject" className = "agenda_project">

            <form className = "agenda-form" onSubmit = {handleSubmit}>
                <h1 className="h1_agenda">Agenda <RiCalendarTodoFill/></h1>

                    <input className = "task_input"
                        type = "text"
                        value = {task}
                        placeholder = "Enter Task" 
                        onChange = {e=>setTask(e.target.value)}
                        />
                    <div className = 'button-wrapper'>
                        <button className = "button_add" type = "submit"> Add </button>
                        <button className = "button_clear" onClick = {handleClear}> Clear </button>
                    </div>
            </form>

            <div className = "display_todo">

                <p>{message}</p>
                    {console.log(tasklist)}
                    {/* {tasklist.length < 14 ? tasklist.map((item)=> 
                        <ul key = {item.id}>

                        <input type = "Checkbox"           
                            name = "isChecked"
                            value = {isChecked}
                            onChange = {setChecked(!isChecked)}
                            />
                            {item.value}
                        </ul>
                        )  : setTask('')}
                    {console.log(tasklist)} */}
            </div>
            <JugPuzzle/>
            <Main/>
            <CurrencyExchange/>
            <OtherConversions/>
        </div>

    )
}
export default AgendaProject
