import React, {Component} from 'react'
import {Link} from "react-scroll"
import {AiOutlineHome,AiOutlineCode} from 'react-icons/ai'
import {FaGraduationCap} from 'react-icons/fa'
import {BsBriefcase} from 'react-icons/bs'
import {RiCalendarTodoFill} from 'react-icons/ri'
///import {GiJug} from 'react-icons/gi'

class NavBar extends Component {
    constructor(){
        super()
        this.state = {
            show: true,
            scrollPos: 0 
        }
    }

    componentDidMount(){
        window.addEventListener('scroll', this.handleScroll)
    }
    componentWillUnmount(){
        window.removeEventListener('scroll', this.handleScroll )
    }
    handleScroll=()=>{
        this.setState({
            scrollPos: document.body.getBoundingClientRect().top,
            show: document.body.getBoundingClientRect().top > this.state.scrollPos
        })
    }

    render(){
        return(

            <div>


                    <nav className = {this.state.show ? 'active' : 'hidden'} >
                        <ul >
                            <li >
                                <Link className = "nav_home"
                                    activeClass = "activeL"
                                    to = "mainpage"
                                    spy = {true}
                                    smooth = {true}
                                    offset = {-120}
                                    duration={20}>
                                    {window.innerWidth < 801 ?  <AiOutlineHome/> : 'Main Page'} 
                                </Link>
                            </li>
                            <li>
                                <Link className = "nav_skills"
                                    activeClass = "activeL"
                                    to = "skills"
                                    spy = {true}
                                    smooth = {true}
                                    offset = {-120}
                                    duration={20}>
                                    {window.innerWidth < 801 ?  <AiOutlineCode/> : 'Skills'} 
                                </Link>
                            </li>
                            <li>
                                <Link className = "nav_ed"
                                    activeClass = "activeL"
                                    to = "education"
                                    spy = {true}
                                    smooth = {true}
                                    offset = {-120}
                                    duration={20}>
                                    {window.innerWidth < 801 ?  <FaGraduationCap/> : 'Education'} 
                                </Link>
                            </li>
                            <li>
                                <Link className = "nav_we"
                                    activeClass = "activeL"
                                    to = "workexperience"
                                    spy = {true}
                                    smooth = {true}
                                    offset = {-120}
                                    duration={20}>
                                    {window.innerWidth < 801 ?  <BsBriefcase/> : 'Work Experience'} 

                                </Link>
                            </li>
                            <li>
                                <Link className = "nav_pr"
                                    activeClass = "activeL"
                                    to = "projects"
                                    spy = {true}
                                    smooth = {true}
                                    offset = {-120}
                                    duration={20}>
                                    {window.innerWidth < 801 ?  <RiCalendarTodoFill/> : 'Projects'} 

                                </Link>
                            </li>


                        </ul>

                    </nav>


            </div>

        )
    }


}

export default NavBar
