import React from 'react'
import Expand from "./Expand"
import Tag from "./Tag"

class Info extends React.Component {

    constructor(props){
        super(props)
        this.state = {

            tags: [],

        }
        this.callBack = this.callBack.bind(this)
    }

    callBack(tagsL, ind){

        this.props.list[ind].tags = [...tagsL, ind]

    }

    render(){
        return(
            <div>

                {this.props.list.map((info)=>{
                if (info.firstName.toLowerCase().includes(this.props.search.toLowerCase()) || 
                    (info.lastName.toLowerCase().includes(this.props.search.toLowerCase()))
                    || (info.tags.includes(this.props.searcht.toLowerCase())))
                        return (
                            <div key = {info.id}>

                                <h1 className = 'name_student'>{info.firstName} {info.lastName}</h1>

                                <img className = "profile_pic_student" src = {info.pic} alt = "profile_pic"/>
                                <div className = "info_div">
                                    <p className ="info"> Email: {info.email}</p>
                                    <p className ="info"> Company: {info.company}</p>
                                    <p className ="info"> Skill: {info.skill}</p>
                                    <p className ="info"> Average: {info.grades.reduce((total, current) => parseInt(total,10) + parseInt(current, 10))/8}%</p>
                                </div>
                                <div className = "expand_container">
                                    <Expand l = {this.props.list.indexOf(info)} li= {this.props.list}/>     
                                </div>
                                <div className ="tag_container">
                                    <Tag l = {this.props.list.indexOf(info)} li= {this.props.list} callBack = {this.callBack}/>

                                </div> 

                            <br/>
                            </div>
                        )

                   return null })}

            </div>    
        )
    }
}
export default Info
