import React, {Component} from 'react'
import {Link} from "react-scroll"

class NavBar extends Component {
    constructor(){
        super()
        this.state = {
            show: true,
            scrollPos: 0 
        }
    }
    componentDidMount(){
        window.addEventListener('scroll', this.handleScroll)
    }
    componentWillUnmount(){
        window.removeEventListener('scroll', this.handleScroll )
    }
    handleScroll=()=>{
        console.log(document.body.getBoundingClientRect ())
    }

    render(){
        return(
            <div>
            <ul className = "navbar">
                        <Link 
                            activeClass = "active"
                            to = "mainpage"
                            spy = {true}
                            smooth = {true}
                            offset = {-70}
                            duration={500}>
                            Main Page
                        </Link>

                        <Link 
                            activeClass = "active"
                            to = "skills"
                            spy = {true}
                            smooth = {true}
                            offset = {-70}
                            duration={500}>
                            Skills
                        </Link>

                        <Link 
                            activeClass = "active"
                            to = "education"
                            spy = {true}
                            smooth = {true}
                            offset = {-70}
                            duration={700}>
                            Education
                        </Link>
                    </ul>
                    <nav>NavBar</nav>
            </div>

        )
    }


}

export default NavBar
